package app.anish.com.tapp.activities;import android.os.Bundle;import android.support.v7.app.ActionBar;import android.support.v7.app.AppCompatActivity;import android.widget.ListView;import com.google.common.collect.Lists;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import app.anish.com.tapp.R;import app.anish.com.tapp.adapters.qr_code.QRCodeSettingsListViewAdapter;import app.anish.com.tapp.adapters.qr_code.lv_item.EmailListViewItem;import app.anish.com.tapp.adapters.qr_code.lv_item.FacebookListViewItem;import app.anish.com.tapp.adapters.qr_code.lv_item.LinkedInListViewItem;import app.anish.com.tapp.adapters.qr_code.lv_item.NameListViewItem;import app.anish.com.tapp.adapters.qr_code.lv_item.PhoneNumberListViewItem;import app.anish.com.tapp.adapters.qr_code.lv_item.QRCodeListViewItem;import app.anish.com.tapp.shared_prefs.SettingsInfo;public class QRCodeSettingsActivity extends AppCompatActivity {    private static final ArrayList<QRCodeListViewItem> listViewItems = Lists.newArrayList(new NameListViewItem(),            new PhoneNumberListViewItem(), new EmailListViewItem(), new FacebookListViewItem(),            new LinkedInListViewItem());    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.settings_layout);        setupActionBar();        initUI();    }    private void initUI() {        ListView listView = (ListView) findViewById(R.id.lvSettings);        QRCodeSettingsListViewAdapter adapter = new QRCodeSettingsListViewAdapter(listViewItems, this);        listView.setAdapter(adapter);    }    private void setupActionBar() {        ActionBar actionBar = getSupportActionBar();        if (actionBar != null) {            actionBar.setDisplayHomeAsUpEnabled(true);        }    }    @Override    public boolean onSupportNavigateUp() {        super.onBackPressed();        finish();        return true;    }}